IdxTy WaitFile(const StrTy & fn, const IdxTy flags=0) const 
{
IdxTy ntries=10;
while (Size(fn)==0)
{
if (ntries==0) return 1;
--ntries;
sleep(1);
} // while 

sleep(1);
return 0; 
} // WaitFile

